CREATE TABLE PERSONAGGI(ID INT GENERATED ALWAYS AS IDENTITY, NOME VARCHAR(30) NOT NULL, ARMATURA SMALLINT DEFAULT 1, LIVELLO SMALLINT DEFAULT 1, PUNTI_FERITA_TOTALI SMALLINT DEFAULT 0, PUNTI_FERITA_ATTUALI SMALLINT DEFAULT 0, SOGLIA_DANNO SMALLINT DEFAULT 0, DIFESA_RIFLESSI SMALLINT DEFAULT 0, DIFESA_TEMPRA SMALLINT DEFAULT 0, DIFESA_VOLONTA SMALLINT DEFAULT 0, ATTACCO_BASE SMALLINT DEFAULT 0, ATTACCO_MISCHIA SMALLINT DEFAULT 0, ATTACCO_DISTANZA SMALLINT DEFAULT 0, VELOCITA SMALLINT DEFAULT 0, INIZIATIVA SMALLINT DEFAULT 0, PERCEZIONE SMALLINT DEFAULT 0, PUNTI_FORZA SMALLINT DEFAULT 0, TALENTI INTEGER[] REFERENCES TALENTI, ATTITUDINI INTEGER[] REFERENCES ATTITUIDINI, POTERI_FORZA INTEGER[] REFERENCES POTERI_FORZA);